import './App.css';
import SideBar from './components/SideBar';
import ChatRoom from './components/ChatRoom';

import firebase from 'firebase/compat/app'
import { useAuthState } from 'react-firebase-hooks/auth'
import 'firebase/compat/firestore'
import 'firebase/compat/auth'


firebase.initializeApp({
  apiKey: "AIzaSyCQIwPV9fPgSahXCirxs0Wb4o3Pbvmwr_s",
  authDomain: "maxmessenger-app.firebaseapp.com",
  projectId: "maxmessenger-app",
  storageBucket: "maxmessenger-app.appspot.com",
  messagingSenderId: "656246182780",
  appId: "1:656246182780:web:46a7a70494f0093e914af9",
  measurementId: "G-TW8N6E6ZKF"
})

const auth = firebase.auth();
const firestore = firebase.firestore();

function App() {
  const [user] = useAuthState(auth);

  return (
    <div className="App">
      <SideBar
      firebase={firebase}
      auth={auth}
      firestore={firestore}
      user={user}
      />

      <ChatRoom
        name="General Chat"
        firebase={firebase}
        auth={auth}
        firestore={firestore}
        user={user}
      />
    </div>
  );
}

export default App;
